/*popup video*/
function youtube_shortcode($atts,$content=null){
	$url = $atts['url'];
	$alt = $atts['alt'];
  	preg_match('%(?:youtube(?:-nocookie)?\.com/(?:[^/]+/.+/|(?:v|e(?:mbed)?)/|.*[?&]v=)|youtu\.be/)([^"&?/ ]{11})%i', $url, $matches);

	if (!empty($matches)) {
		$img = $atts['img'];
		return '<a data-video="'.$matches[1].'" class="modal-clip popClip" rel="nofollow"><img width="550" height="307" src="'.$img.'" alt="'.$alt.'"></a>';
	}
}
add_shortcode('youtube','youtube_shortcode');
/*End Popup video*/

